function textCounter(){var count=document.getElementById("textInput").value.length;document.getElementById("charCount").innerHTML=count,document.getElementById("charCount").style.color=0==count?"black":count<=100?"green":count>100&&count<=130?"orange":count>130&&count<140?"#e24a00":"red",document.getElementById("footDiv").innerHTML=140==count?"Character count reached maximum.":"Maximum 140 characters."}function clearInput(){textInput.value="",textCounter(),textInput.focus()}